package matrix; import javax.swing.*; import java.lang.String; import static java.lang.Integer.parseInt; public class ěden_řádek { public static void main(String[] args) { int[][] array = new int[5][5]; String rozsah_i = JOptionPane.showInputDialog("Zadej maximální číslo:"); String rozsah_j = JOptionPane.showInputDialog("Zadej maximální číslo:"); String max_cislo = JOptionPane.showInputDialog("Zadej maximální číslo:"); System.out.println("Maximální číslo: " + max_cislo); for (int i = 0; i < array.length; i++) { for (int j = 0; j < array[i].length; j++) { array[i][j] = (int) (Math.random() * parseInt(max_cislo) + 1); System.out.print(array[i][j] + "   "); } System.out.println(); } } }